import React, { Component } from 'react'
import axios from 'axios'

export class HTTPPost extends Component {
    constructor(props) {
      super(props)

      this.state = {
        apiResponse: null,
      }
    }

    componentDidMount() {}

    postToApi = () => {
        axios.post('https://jsonplaceholder.typicode.com/posts', 
            {
                title: 'Hello world!',
                body: 'It works!',
                userId: 123,
            }
        ).then(response => {
            this.setState({
                posts: Array.isArray(response.data)
                ? response.data
                : [response.data]
            })
        })
        .catch(error => {
            this.setState({
                error: error.message
            })
        })
    }

    render() {
        return (
        <div>
            <button onClick={this.postToApi}>
                Create Post
            </button>
            <h2>Posts:</h2>
            {
                posts.length ? (
                    posts.map(post => (
                        <div key={post.id}>
                            <h2>{post.id}. {post.title}</h2>
                            <h4>By User ID {post.userId}</h4>
                            <p>{post.body}</p>
                            <hr />
                        </div>
                    ))
                ) : (
                    this.state.error 
                    ? <p>{this.state.error}</p>
                    : <h4>Loading posts ...</h4>
                )
            }
        </div>
        )
    }
}

export default HTTPPost
