import React, { Component } from 'react'
import axios from 'axios'

export class HTTPRequest extends Component {
    constructor(props) {
        super(props)

        this.state = {
            posts: []
        }
    }

    componentDidMount() {
        axios.get('https://jsonplaceholder.typicode.com/posts')
        .then(response => {
            this.setState({
                posts: response.data
            })
        })
    }

    render() {
        const posts = this.state.posts;
        return (
        <div>
            <h2>Posts:</h2>
            {
                posts.length ? (
                    posts.map(post => (
                        <div key={post.id}>
                            <h2>{post.id}</h2>
                        </div>
                    ))
                ) : (
                    <h4>Loading posts</h4>
                )
            }
        </div>
        )
    }
}

export default HTTPRequest
